<template>
  <a-divider
    style="font-size: 30px; !important"
  >
    Dashboard Widget
  </a-divider>
  <a-button class="responsive-bt" type="primary" @click="addNewWidget()">Add Widget</a-button>
  <section class="grid-stack"></section>  
  <!-- <a-divider>
    {{ info }}
  </a-divider> -->
</template>

<script lang="ts">
import { defineComponent, onMounted, ref } from "vue";
import { GridStack } from 'gridstack';
// import { message } from "ant-design-vue";

export default defineComponent({
  name: 'Dashboard',
  setup () {
    let count = ref(0);
    let info = ref("");
    let grid: GridStack;
    const items = [
      { x: 2, y: 1, h: 2 },
      { x: 2, y: 4, w: 3 },
      { x: 4, y: 2 },
      { x: 3, y: 1, h: 2 },
      { x: 0, y: 6, w: 2, h: 2 },
    ];

    onMounted(() => {
      grid = GridStack.init({
        float: true,
        cellHeight: "70px",
        minRow: 3,
      });

      // message.info(`Column: ${grid.getColumn()}, Row: ${grid.getRow()}`);

      // Use an arrow function so that `this` is bound to the Vue instance. Alternatively, use a custom Vue directive on the `.grid-stack` container element: https://vuejs.org/v2/guide/custom-directive.html
      grid.on("dragstop", (event, element: any) => {
        const node = element.gridstackNode;
        // `this` will only access your Vue instance if you used an arrow function, otherwise `this` binds to window scope. see https://hacks.mozilla.org/2015/06/es6-in-depth-arrow-functions/
        info.value = `you just dragged node #${node.id} to ${node.x},${node.y} â€“ good job!`;
      });
    });

    function addNewWidget() {
      const node: any = items[count.value] || {
        x: Math.round(12 * Math.random()),
        y: Math.round(5 * Math.random()),
        w: Math.round(1 + 3 * Math.random()),
        h: Math.round(1 + 3 * Math.random()),
      };
      node.id = node.content = String(count.value++);
      grid.addWidget('<div class="grid-stack-item"><div class="grid-stack-item-content"><button style="margin: 5px;" type="button" >Button Dummy</button></div></div>', node);
    }

    return {
      info,
      addNewWidget,
    };
  }
})
</script>

